import React from "react";

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = { activeTab: "home" };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(param) {
    this.setState(state => ({
      activeTab: param
    }));
    console.log(param);
  }

  render() {
    return (
      <div className="HeaderWrapper">
        <h1>EXPO</h1>
        <p onClick={this.handleClick.bind(this, "information")}>Information</p>
        <button onClick={this.handleClick.bind(this, "InterviewLocations")}>
          Interview Locations
        </button>
        <button onClick={this.handleClick.bind(this, "Volunteer")}>
          Volunteer
        </button>
        <button onClick={this.handleClick.bind(this, "Committee")}>
          Committee
        </button>
      </div>
    );
  }
}

